mapper.select:
select

from zh_qywx_cust_rel
where qywx_cust_id = #{qywxCustId}
and qywx_mgr_id = #{qywxMgrId}
and corp_id = #{corpId}

mapper.update:
update zh_qywx_cust_rel set

    remark = #{remark},

gmt_modified = now()
where qywx_cust_id = #{qywxCustId}
and qywx_mgr_id = #{qywxMgrId}
and corp_id = #{corpId}
